var Mosaic={controller:null,init:function(){Mosaic.controller=new ScrollMagic.Controller,$(document).find(".mosaic-component .block-container-img:not(.isAuthor)").each(function(){$(document).find(".card-es-component").parent().prev().hasClass("hero-moments")||$(document).find(".card-es-component").parent().prev().hasClass("hero")||Mosaic.loadAnimate($(this))})},loadAnimate:function(e){var o,n;0<e.length&&(o=e.height()/2,n=TweenMax.from(e,.5,{opacity:0,ease:Power0.easeNone}),new ScrollMagic.Scene({triggerElement:e[0],duration:o,reverse:!1,triggerHook:.8}).setTween(n).addTo(Mosaic.controller))}};$(document).ready(function(){Mosaic.init()});