var ContentBlockES={controller:null,init:function(){ContentBlockES.controller=new ScrollMagic.Controller,$(document).find(".content-block-component:not(.isAuthor)").each(function(){$(document).find(".content-block-component").parent().prev().hasClass("hero-moments")||$(document).find(".content-block-component").parent().prev().hasClass("hero")||$(this).hasClass("animate-fade")&&ContentBlockES.fadeSimple($(this)),ContentBlockES.checkPohotoInvoice($(this))})},fadeSimple:function(o){var e=TweenMax.fromTo(o,.5,{opacity:"0",ease:Power0.easeNone},{opacity:"1",ease:Power0.easeNone},.2);new ScrollMagic.Scene({triggerElement:o[0],reverse:!1,triggerHook:.8}).setTween(e).addTo(ContentBlockES.controller)},checkPohotoInvoice:function(o){0<($this=o).find(".block-photo-invoice").length&&($this.find(".block-photo-invoice .help, .component-title, .component-description").remove(),$this.find(".photo-invoice-content").removeClass("fullwidth"))}};$(document).ready(function(){0===$(".list-style-weather").length&&ContentBlockES.init()});